import { ArrowLeft, ArrowRight } from '@material-ui/icons'
import React from 'react'
import styled from 'styled-components'

const Container = styled.div`
    width: 100%;
    height: 100vh;
    display: flex;
    position: relative;
`

const Display = styled.div`
    height: 100%;
`

const Slider = styled.div`
    display: flex;
    align-items: center;
    width: 100vw;
    height: 100vh;
`
const ImageContainer = styled.div`
    flex: 1;
    height: 100%;
`
const Image = styled.img`
    height: 80%;
`

const InformationContainer = styled.div`
    flex: 1;
    padding: 50px;
`
const Title = styled.h1`
    font-size: 40px;
    letter-spacing: 2px;
`
const Description = styled.p`
    margin: 20px 0px;
    font-size: 25px;
`
const Button = styled.button`
    padding
`

const Arrow = styled.div`
    position: absolute;
    top: 0;
    bottom: 0;
    margin: auto;
    left: ${props=> props.direction === "left" && "20px"};
    right: ${props=> props.direction === "right" && "20px"};
    background-color: whitesmoke;
    width: 60px;
    height: 60px;
    border-radius: 50%;
    opacity: 0.45;
    display: flex;
    justify-content: center;
    align-items: center;
    cursor: pointer;
`

const HomeSlide = () => {
  return (
    <Container>
        <Arrow direction="left">
            <ArrowLeft/>
        </Arrow>
        <Display>
            <Slider>
                <ImageContainer>
                    <Image src="https://images.unsplash.com/photo-1515886657613-9f3515b0c78f?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8bW9kZWx8ZW58MHx8MHx8&auto=format&fit=crop&w=500&q=60"/>
                </ImageContainer>
                <InformationContainer>
                    <Title>New collection</Title>
                    <Description>No compromise!</Description>
                    <Button>LET'S SHOP!</Button>
                </InformationContainer>
            </Slider>
        </Display>
        <Arrow direction="right">
            <ArrowRight/>
        </Arrow>
    </Container>
  )
}

export default HomeSlide
