import { Add, Remove } from "@material-ui/icons"
import styled from "styled-components"
import Adds from "../comps/Adds"
import Footer from "../comps/Footer"
import Navbar from "../comps/Navbar"

const Container = styled.div``
const Wrapper = styled.div`
    padding: 20px;
`
const Title = styled.h1`
    font-weight: 300;
    text-align: center;
`
const Top = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 20px;
`
const TopButton = styled.button`
    padding: 10px;
    font-weight: 600;
    font-size: 24px;
    cursor: pointer;
    border: ${(props)=>props.type === "filled" && "none"};
    background-color: ${(props)=>props.type === "filled" ? "black" : "transparent"};
    color: ${(props)=>props.type === "filled" && "white"};
`
const TopTexts = styled.div``
const TopText = styled.span`
    text-decoration: underline;
    cursor: pointer;
    font-size: 20px;
    margin: 0px 20px;
`
const Bottom = styled.div`
    display: flex;
    justify-content: space-between;
`
const Info = styled.div`
    flex: 3;
`
const Product = styled.div`
    display: flex;
    justify-content: space-between;
`
const ProductDetail = styled.div`
    flex: 2;
    display: flex;
`
const Image = styled.img`
    width: 200px;
    height: 200px;
    object-fit: cover;
`
const Details = styled.div`
    padding: 20px;
    display: flex;
    flex-direction: column;
    justify-content: space-around;
    font-size: 20px;
`
const ProductName = styled.span``
const ProductId = styled.span``
const ProductColor = styled.div`
    width: 20px;
    height: 20px;
    border-radius: 50%;
    background-color: ${props=>props.color};
`
const ProductSize = styled.span``
const PriceDetail = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
`
const ProductAmountContainer = styled.div`
    display: flex;
    align-items: center;
    margin-bottom: 20px;
`
const ProductAmount = styled.div`
    font-size: 24px;
    margin: 5px;
`
const ProductPrice = styled.div`
    font-size: 30px;
    font-weight: 200;
`
const Hr = styled.hr`
    background-color: #eee;
    border: none;
    height: 3px;
`
const Summary = styled.div`
    flex: 1;
`
const SummaryTitle = styled.div``
const SummaryItem = styled.div``
const SummaryItemText = styled.div``


const Cart = () => {
  return (
    <Container>
        <Navbar/>
        <Adds/>
        <Wrapper>
            <Title>Your bag</Title>
            <Top>
                <TopButton>Continue shopping</TopButton>
                <TopTexts>
                    <TopText>Shopping bag(2)</TopText>
                    <TopText>Your wishlist(0)</TopText>
                </TopTexts>
                <TopButton type="filled">Checkout now</TopButton>
            </Top>
            <Bottom>
                <Info>
                    <Product>
                        <ProductDetail>
                            <Image src="https://images.pexels.com/photos/2068349/pexels-photo-2068349.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"/>
                            <Details>
                                <ProductName><b>Product:</b> Denim Jeans</ProductName>
                                <ProductId><b>ID:</b> 123</ProductId>
                                <ProductColor color ="lightblue"/>
                                <ProductSize><b>Size:</b> M</ProductSize>
                            </Details>
                        </ProductDetail>
                        <PriceDetail>
                            <ProductAmountContainer>
                                <Add/>
                                <ProductAmount>2</ProductAmount>
                                <Remove/>
                            </ProductAmountContainer>
                            <ProductPrice>$ 20</ProductPrice>
                        </PriceDetail>
                    </Product>
                    <Hr/>
                    <Product>
                        <ProductDetail>
                            <Image src="https://images.pexels.com/photos/1537671/pexels-photo-1537671.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"/>
                            <Details>
                                <ProductName><b>Product:</b> Runner shoes</ProductName>
                                <ProductId><b>ID:</b> 199</ProductId>
                                <ProductColor color ="yellow"/>
                                <ProductSize><b>Size:</b> 38</ProductSize>
                            </Details>
                        </ProductDetail>
                        <PriceDetail>
                            <ProductAmountContainer>
                                <Add/>
                                <ProductAmount>2</ProductAmount>
                                <Remove/>
                            </ProductAmountContainer>
                            <ProductPrice>$ 30</ProductPrice>
                        </PriceDetail>
                    </Product>
                </Info>
                <Summary>
                    <SummaryTitle>Order summary</SummaryTitle>
                    <SummaryItem>
                        <SummeryItemText>Subtotal</SummeryItemText>
                        <SummeryItemPrice>$ 50</SummeryItemPrice>
                    </SummaryItem>
                    <SummaryItem>
                        <SummeryItemText>Estimated shipping</SummeryItemText>
                        <SummeryItemPrice>$ 5.00</SummeryItemPrice>
                    </SummaryItem>
                    <SummaryItem>
                        <SummeryItemText>Shipping discount</SummeryItemText>
                        <SummeryItemPrice>$ -5.00</SummeryItemPrice>
                    </SummaryItem>
                    <SummaryItem>
                        <SummeryItemText type="total">Total</SummeryItemText>
                        <SummeryItemPrice>$ 50</SummeryItemPrice>
                    </SummaryItem>
                    <Button>Checkout now</Button>
                </Summary>
            </Bottom>
        </Wrapper>
        <Footer/>
    </Container>
  )
}

export default Cart
