                <ImageContainer>
                    <Image src="https://o.remove.bg/downloads/efa2a9ba-85f2-40c2-8cef-65d4b7894a97/shutterstock_266558153-removebg-preview.png"/>
                </ImageContainer>
                <InformationContainer>
                    <Title>New collection</Title>
                    <Description>No compromise!</Description>
                    <Button>LET'S SHOP!</Button>
                </InformationContainer>
            </Slider>
            <Slider bg="b1c7cd">
