                </ImageContainer>
                <InformationContainer>
                    <Title>New collection</Title>
                    <Description>Inpired by the best!</Description>
                    <Button>LET'S SHOP!</Button>
                </InformationContainer>
            </Slider>
        </Display>
        <Arrow direction="right" onClick={()=>handleClick("right")}>
